var total = 0; function add2Cart(e, t) { var n = document.getElementById("TableCart").insertRow(); n.insertCell().innerHTML = e, n.insertCell().innerHTML = "$" + t, total += t, document.getElementById("purchaseInfo").innerHTML = "Total: $" + total } function displayTable() { document.getElementById("hideShow").style.display = "block" } function Rensa() { confirm("Are you sure you want to clear your cart?") && (total = 0, document.getElementById("purchaseInfo").innerHTML = "Total: $" + total, document.getElementById("TableCart").innerHTML = "<tr><td>Name</td><td>Pris</td></tr>", 0 == total && (document.getElementById("hideShow").style.display = "none")) } var ix = 0; function increaseNum() { document.getElementById("increase").value = ++ix } function checkOut() { alert("Your order has been sent! Thank you!") }